import React, { useState, useEffect } from 'react';
import "./HomeScreen.css";

export default function HomeScreen() {
  const [mountains, setMountains] = useState([]);

  const generateMountains = () => {
    let leftPos = 0;
    const arr = [];
    while (leftPos < 300) {
      const isFront = arr.length % 2 === 0;
      const h = isFront 
        ? Math.floor(Math.random() * 150) + 1750 
        : Math.floor(Math.random() * 150) + 2650;
      const w = Math.floor(Math.random() * 200) + 1900;
      const s = Math.random() * 3;
      const l = leftPos + s;
      arr.push({ height: h, width: w, left: `${l}%`, isFront });
      leftPos = l + 9.5;
    }
    const mountainBefore = {
      height: Math.floor(Math.random() * 150) + 1750,
      width: Math.floor(Math.random() * 200) + 1900,
      left: "-5%",
      isFront: true
    };
    const mountainAfter = {
      height: Math.floor(Math.random() * 150) + 1750,
      width: Math.floor(Math.random() * 200) + 1900,
      left: "305%",
      isFront: false
    };
    arr.unshift(mountainBefore);
    arr.push(mountainAfter);
    return arr;
  };

  useEffect(() => {
    setMountains(generateMountains());
  }, []);

  const rayCount = 18;
  const orbitRadius = 46;
  const minRayLength = 20;
  const maxRayLength = 25;
  const rays = Array.from({ length: rayCount }, (_, i) => {
    const angle = i * (360 / rayCount);
    const rayLength = Math.random() * (maxRayLength - minRayLength) + minRayLength;
    const randomDelay = Math.random() * 8;
    const randomDuration = Math.random() * 2 + 3;
    return (
      <div
        key={i}
        className="sun-ray"
        style={{
          width: `${rayLength}px`,
          transform: `rotate(${angle}deg) translateX(${orbitRadius}px)`,
          '--lineLength': `${rayLength}px`,
          animation: `bounce ${randomDuration}s linear infinite`,
          animationDelay: `${randomDelay}s`
        }}
      />
    );
  });

  function toggleFullscreen() {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
    } else if (document.exitFullscreen) {
      document.exitFullscreen();
    }
  }

  // Generates a smooth, realistic wave path for the water's top curve.
  // The baseline is set at y = 300 and we use 6 segments with cubic Bezier curves.
  // 'shift' is added to simulate horizontal motion.
  function generateWavePath(shift) {
    const baseline = 300;
    let d = `M0 ${baseline}`;
    const segments = 6;
    const segmentWidth = 1200 / segments;
    for (let i = 0; i < segments; i++) {
      const x0 = i * segmentWidth;
      const x1 = x0 + segmentWidth / 3;
      const x2 = x0 + (2 * segmentWidth) / 3;
      const x3 = (i + 1) * segmentWidth;
      // Alternate amplitude for a natural wave effect.
      const amplitude = 20 * (i % 2 === 0 ? 1 : -1);
      const cp1X = x1 + shift;
      const cp2X = x2 + shift;
      const cp1Y = baseline + amplitude;
      const cp2Y = baseline + amplitude;
      d += ` C ${cp1X.toFixed(1)} ${cp1Y.toFixed(1)}, ${cp2X.toFixed(1)} ${cp2Y.toFixed(1)}, ${x3} ${baseline}`;
    }
    // Close the path by drawing the fixed bottom of the water.
    d += ` L1200 800 L0 800 Z`;
    return d;
  }

  // d1 is the base wave path and d2 is the shifted version.
  const d1 = generateWavePath(0);
  const randomShift = Math.random() * 30 + 20; // shift between 20 and 50
  const d2 = generateWavePath(randomShift);

  // Helper to generate a thin white curvy line for the current.
  // The line will be short (~200px long) and spawn at a random vertical position
  // between 600 and 700 (i.e. from 400 below the water's top to 200 above the bottom).
  function generateWhiteLinePath() {
    const baseline = Math.random() * 100 + 600; // random y between 600 and 700
    let d = `M -50 ${baseline}`;
    const segments = 2;
    const totalWidth = 200; // short current line
    const segmentWidth = totalWidth / segments;
    for (let i = 0; i < segments; i++) {
      const startX = -50 + i * segmentWidth;
      const endX = -50 + (i + 1) * segmentWidth;
      const cpX = (startX + endX) / 2 + (Math.random() * 20 - 10);
      const cpY = baseline + (Math.random() * 20 - 10);
      d += ` Q ${cpX.toFixed(1)} ${cpY.toFixed(1)}, ${endX} ${baseline}`;
    }
    return d;
  }

  // Create an array of white current lines.
  // They animate with a horizontal translation to simulate current motion.
  const whiteLines = Array.from({ length: 4 }, (_, i) => {
    const d = generateWhiteLinePath();
    return (
      <path key={i} d={d} stroke="white" strokeWidth="1" fill="none">
        <animateTransform 
          attributeName="transform" 
          type="translate" 
          from="0 0" 
          to="30 0" 
          dur="4s" 
          repeatCount="indefinite" 
        />
      </path>
    );
  });

  return (
    <div id="homescreen">
      <div className="mountain-container">
        {mountains.map((m, i) => {
          const style = {
            position: "absolute",
            bottom: `-${m.height / 1.2}px`,
            left: m.left,
            width: `${m.width}px`,
            height: `${m.height}px`,
            background: `radial-gradient(circle at 50% 80%, #158b34 20%, #0f6f28 85%, #0b4d1e 100%)`,
            boxShadow: `0 20px 50px rgba(0, 0, 0, 0.4), inset 0 -20px 30px rgba(0, 0, 0, 0.5)`,
            borderRadius: "50% 50% 20% 20%",
            border: "8px solid rgba(0, 0, 0, 0.5)",
            zIndex: m.isFront ? 2 : 1
          };
          return <div key={i} style={style} />;
        })}
      </div>
      <div className="brick-wall"></div>
      <div className="sun">
        {rays}
      </div>
      <div className="fullscreen-icon">
        <div className="hitbox" onClick={toggleFullscreen}></div>
        <div className="corner top-left"></div>
        <div className="corner top-right"></div>
        <div className="corner bottom-right"></div>
        <div className="corner bottom-left"></div>
      </div>
      <div className="water">
        <svg viewBox="0 0 1200 800" preserveAspectRatio="none">
          <path d={d1} fill="#0af" fillOpacity="0.6">
            {/* Animate the top wave's shape */}
            <animate 
              attributeName="d" 
              dur="5s" 
              repeatCount="indefinite" 
              values={`${d1};${d2};${d1}`} 
            />
            {/* Subtle horizontal translation */}
            <animateTransform 
              attributeName="transform" 
              type="translate" 
              from="0 0" 
              to="30 0" 
              dur="5s" 
              repeatCount="indefinite" 
              additive="sum" 
            />
          </path>
          {/* White curvy lines showing currents */}
          {whiteLines}
        </svg>
      </div>
    </div>
  );
}
