import React, { useState, useEffect, useRef } from 'react';
import './HomeScreen.css';
import player1Img from './assets/player1.png';
import player2Img from './assets/player2.png';

export default function HomeScreen() {
  const [mountains, setMountains] = useState([]);
  const player1Ref = useRef(null);
  const player2Ref = useRef(null);
  const brickWallRef = useRef(null);
  const keys = useRef({});
  const p1 = useRef({ pos: { x: 100, y: 200 }, vel: { x: 0, y: 0 }, grounded: false });
  const p2 = useRef({ pos: { x: 300, y: 200 }, vel: { x: 0, y: 0 }, grounded: false });

  useEffect(() => {
    if (player1Ref.current) {
      player1Ref.current.style.transform = `translate(${p1.current.pos.x}px, ${p1.current.pos.y}px)`;
    }
    if (player2Ref.current) {
      player2Ref.current.style.transform = `translate(${p2.current.pos.x}px, ${p2.current.pos.y}px)`;
    }
  }, []);

  const generateMountains = () => {
    let leftPos = 0;
    const arr = [];
    while (leftPos < 300) {
      const isFront = arr.length % 2 === 0;
      const h = isFront ? Math.floor(Math.random() * 150) + 1750 : Math.floor(Math.random() * 150) + 2650;
      const w = Math.floor(Math.random() * 200) + 1900;
      const s = Math.random() * 3;
      const l = leftPos + s;
      arr.push({ height: h, width: w, left: `${l}%`, isFront });
      leftPos = l + 9.5;
    }
    const mountainBefore = { height: Math.floor(Math.random() * 150) + 1750, width: Math.floor(Math.random() * 200) + 1900, left: '-5%', isFront: true };
    const mountainAfter = { height: Math.floor(Math.random() * 150) + 1750, width: Math.floor(Math.random() * 200) + 1900, left: '305%', isFront: false };
    arr.unshift(mountainBefore);
    arr.push(mountainAfter);
    return arr;
  };
  useEffect(() => {
    setMountains(generateMountains());
  }, []);
  useEffect(() => {
    const handleKeyDown = e => {
      keys.current[e.key] = true;
    };
    const handleKeyUp = e => {
      keys.current[e.key] = false;
    };
    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, []);
  useEffect(() => {
    let frameId;
    const gravity = 0.25;
    const friction = 0.85;
    const acceleration = 0.1;
    const jumpForce = 7;
    const playerWidth = 50;
    const playerHeight = 80;
    const loop = () => {
      const homeScreen = document.getElementById('homescreen');
      const homeRect = homeScreen.getBoundingClientRect();

      if ((keys.current['w'] || keys.current['W']) && p1.current.grounded) {
        p1.current.vel.y = -jumpForce;
        p1.current.grounded = false;
      }
      if (keys.current['a'] || keys.current['A']) {
        p1.current.vel.x -= acceleration;
      }
      if (keys.current['d'] || keys.current['D']) {
        p1.current.vel.x += acceleration;
      }

      if (keys.current['ArrowUp'] && p2.current.grounded) {
        p2.current.vel.y = -jumpForce;
        p2.current.grounded = false;
      }
      if (keys.current['ArrowLeft']) {
        p2.current.vel.x -= acceleration;
      }
      if (keys.current['ArrowRight']) {
        p2.current.vel.x += acceleration;
      }

      p1.current.vel.y += gravity;
      p2.current.vel.y += gravity;

      p1.current.vel.x *= friction;
      p1.current.vel.y *= friction;
      p2.current.vel.x *= friction;
      p2.current.vel.y *= friction;

      p1.current.pos.x += p1.current.vel.x;
      p1.current.pos.y += p1.current.vel.y;
      p2.current.pos.x += p2.current.vel.x;
      p2.current.pos.y += p2.current.vel.y;

      if (brickWallRef.current) {
        const wallRect = brickWallRef.current.getBoundingClientRect();
        const wallBounds = {
          top: wallRect.top - homeRect.top,
          bottom: wallRect.bottom - homeRect.top,
          left: wallRect.left - homeRect.left,
          right: wallRect.right - homeRect.left,
        };

        handleCollision(p1.current, wallBounds, playerWidth, playerHeight);
        handleCollision(p2.current, wallBounds, playerWidth, playerHeight);
      }

      keepInBounds(p1.current, homeRect, playerWidth, playerHeight);
      keepInBounds(p2.current, homeRect, playerWidth, playerHeight);

      if (player1Ref.current) {
        player1Ref.current.style.transform = `translate(${p1.current.pos.x}px, ${p1.current.pos.y}px)`;
      }
      if (player2Ref.current) {
        player2Ref.current.style.transform = `translate(${p2.current.pos.x}px, ${p2.current.pos.y}px)`;
      }

      frameId = requestAnimationFrame(loop);
    };

    frameId = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(frameId);
  }, []);

  function handleCollision(player, wallBounds, playerWidth, playerHeight) {
    const playerLeft = player.pos.x;
    const playerRight = player.pos.x + playerWidth;
    const playerTop = player.pos.y;
    const playerBottom = player.pos.y + playerHeight;

    const isColliding =
      playerRight > wallBounds.left &&
      playerLeft < wallBounds.right &&
      playerBottom > wallBounds.top &&
      playerTop < wallBounds.bottom;

    if (isColliding) {
      const overlapX =
        player.vel.x > 0
          ? wallBounds.left - playerRight
          : wallBounds.right - playerLeft;

      const overlapY =
        player.vel.y > 0
          ? wallBounds.top - playerBottom
          : wallBounds.bottom - playerTop;

      if (Math.abs(overlapX) < Math.abs(overlapY)) {
        player.pos.x += overlapX;
        player.vel.x = 0;
      } else {
        player.pos.y += overlapY;
        player.vel.y = 0;
        if (overlapY < 0) {
          player.grounded = true;
        }
      }
    } else {
      if (player.pos.y + playerHeight >= wallBounds.top) {
        player.grounded = false;
      }
    }
  }

  function keepInBounds(player, homeRect, playerWidth, playerHeight) {
    const gameWidth = homeRect.width;
    const gameHeight = homeRect.height;

    if (player.pos.x < 0) {
      player.pos.x = 0;
      player.vel.x = 0;
    }
    if (player.pos.x + playerWidth > gameWidth) {
      player.pos.x = gameWidth - playerWidth;
      player.vel.x = 0;
    }
    if (player.pos.y < 0) {
      player.pos.y = 0;
      player.vel.y = 0;
    }
    if (player.pos.y + playerHeight > gameHeight) {
      player.pos.y = gameHeight - playerHeight;
      player.vel.y = 0;
      player.grounded = true;
    }
  }

  const rayCount = 18;
  const orbitRadius = 46;
  const minRayLength = 20;
  const maxRayLength = 25;
  const rays = Array.from({ length: rayCount }, (_, i) => {
    const angle = i * (360 / rayCount);
    const rayLength = Math.random() * (maxRayLength - minRayLength) + minRayLength;
    const randomDelay = Math.random() * 8;
    const randomDuration = Math.random() * 2 + 3;
    return (
      <div
        key={i}
        className="sun-ray"
        style={{
          width: `${rayLength}px`,
          transform: `rotate(${angle}deg) translateX(${orbitRadius}px)`,
          '--lineLength': `${rayLength}px`,
          animation: `bounce ${randomDuration}s linear infinite`,
          animationDelay: `${randomDelay}s`,
        }}
      />
    );
  });
  const generateWavePath = shift => {
    const baseline = 300;
    let d = `M0 ${baseline}`;
    const segments = 6;
    const segmentWidth = 1200 / segments;
    for (let i = 0; i < segments; i++) {
      const x0 = i * segmentWidth;
      const x1 = x0 + segmentWidth / 3;
      const x2 = x0 + (2 * segmentWidth) / 3;
      const x3 = (i + 1) * segmentWidth;
      const amplitude = 20 * (i % 2 === 0 ? 1 : -1);
      const cp1X = x1 + shift;
      const cp2X = x2 + shift;
      const cp1Y = baseline + amplitude;
      const cp2Y = baseline + amplitude;
      d += ` C ${cp1X.toFixed(1)} ${cp1Y.toFixed(1)}, ${cp2X.toFixed(1)} ${cp2Y.toFixed(1)}, ${x3} ${baseline}`;
    }
    d += ` L1200 800 L0 800 Z`;
    return d;
  };
  const d1 = generateWavePath(0);
  return (
    <div id="homescreen">
      <div className="mountain-container">
        {mountains.map((m, i) => {
          const style = {
            position: 'absolute',
            bottom: `-${m.height / 1.2}px`,
            left: m.left,
            width: `${m.width}px`,
            height: `${m.height}px`,
            background: `radial-gradient(circle at 50% 80%, #158b34 20%, #0f6f28 85%, #0b4d1e 100%)`,
            boxShadow: `0 20px 50px rgba(0,0,0,0.4), inset 0 -20px 30px rgba(0,0,0,0.5)`,
            borderRadius: '50% 50% 20% 20%',
            border: '8px solid rgba(0,0,0,0.5)',
            zIndex: m.isFront ? 2 : 1,
          };
          return <div key={i} style={style} />;
        })}
      </div>
      <div className="brick-wall" ref={brickWallRef}></div>
      <div className="sun">{rays}</div>
      <div className="fullscreen-icon">
        <div
          className="hitbox"
          onClick={() => {
            if (!document.fullscreenElement) {
              document.documentElement.requestFullscreen();
            } else if (document.exitFullscreen) {
              document.exitFullscreen();
            }
          }}
        ></div>
        <div className="corner top-left"></div>
        <div className="corner top-right"></div>
        <div className="corner bottom-right"></div>
        <div className="corner bottom-left"></div>
      </div>
      <div className="overlay">
        <div className="text-container">
          <div className="top-text">ECO</div>
          <div className="bottom-text">RESCUE</div>
        </div>
        <div className="pyramid-buttons">
          <button className="menu-button">Start</button>
          <div className="lower-buttons">
            <button className="menu-button">Settings</button>
            <button className="menu-button">Keybinds</button>
          </div>
        </div>
      </div>
      <div className="water">
        <svg viewBox="0 0 1200 800" preserveAspectRatio="none">
          <path d={d1} fill="#0af" fillOpacity="0.6" />
        </svg>
      </div>
      <img
        ref={player1Ref}
        src={player1Img}
        alt="Player 1"
        className="player player1"
        style={{ width: `${playerWidth}px`, height: `${playerHeight}px` }}
      />
      <img
        ref={player2Ref}
        src={player2Img}
        alt="Player 2"
        className="player player2"
        style={{ width: `${playerWidth}px`, height: `${playerHeight}px` }}
      />
    </div>
  );
}
