import React, { useState, useEffect } from 'react';
import "./HomeScreen.css";

export default function HomeScreen() {
  const [mountains, setMountains] = useState([]);
  const [currents, setCurrents] = useState([]);

  const generateMountains = () => {
    let leftPos = 0;
    const arr = [];
    while (leftPos < 300) {
      const isFront = arr.length % 2 === 0;
      const h = isFront ? Math.floor(Math.random() * 150) + 1750 : Math.floor(Math.random() * 150) + 2650;
      const w = Math.floor(Math.random() * 200) + 1900;
      const s = Math.random() * 3;
      const l = leftPos + s;
      arr.push({ height: h, width: w, left: `${l}%`, isFront });
      leftPos = l + 9.5;
    }
    const mountainBefore = {
      height: Math.floor(Math.random() * 150) + 1750,
      width: Math.floor(Math.random() * 200) + 1900,
      left: "-5%",
      isFront: true
    };
    const mountainAfter = {
      height: Math.floor(Math.random() * 150) + 1750,
      width: Math.floor(Math.random() * 200) + 1900,
      left: "305%",
      isFront: false
    };
    arr.unshift(mountainBefore);
    arr.push(mountainAfter);
    return arr;
  };

  const generateCurrentLine = () => {
    // Generate a more curvy, bulging wave with random control points
    const startX = Math.random() * 30 - 50;           // from -50 to -20
    const endX = Math.random() * 100 + 1100;            // from 1100 to 1200
    const startY = Math.random() * 20 + 80;             // 80 to 100
    const endY = startY + (Math.random() * 20 - 10);      // slight variation in Y
    const cp1X = startX + Math.random() * 200 + 100;      // ensure a bulge
    const cp1Y = startY + Math.random() * 40 - 20;
    const cp2X = endX - Math.random() * 200 - 100;
    const cp2Y = endY + Math.random() * 40 - 20;
    const d1 = `M${startX},${startY} C${cp1X},${cp1Y} ${cp2X},${cp2Y} ${endX},${endY}`;
    const d2 = `M${startX},${startY} C${cp1X+10},${cp1Y+10} ${cp2X-10},${cp2Y-10} ${endX},${endY}`;
    const duration = (Math.random() * 2 + 3).toFixed(1); // 3-5s duration
    const delay = (Math.random() * 1).toFixed(1);
    return { d1, d2, duration, delay };
  };

  const generateCurrents = (count) => {
    let arr = [];
    for (let i = 0; i < count; i++) {
      arr.push(generateCurrentLine());
    }
    return arr;
  };

  useEffect(() => {
    setMountains(generateMountains());
    setCurrents(generateCurrents(10)); // more currents spread throughout the water
    const handleResize = () => {
      setMountains(generateMountains());
    };
    window.addEventListener("resize", handleResize);
    // Every cycle, regenerate currents so that as soon as one goes offscreen, new curves appear
    const interval = setInterval(() => {
      setCurrents(generateCurrents(10));
    }, 5000);
    return () => {
      window.removeEventListener("resize", handleResize);
      clearInterval(interval);
    };
  }, []);

  const rayCount = 18;
  const orbitRadius = 46;
  const minRayLength = 20;
  const maxRayLength = 25;
  const rays = Array.from({ length: rayCount }, (_, i) => {
    const angle = i * (360 / rayCount);
    const rayLength = Math.random() * (maxRayLength - minRayLength) + minRayLength;
    const randomDelay = Math.random() * 8;
    const randomDuration = Math.random() * 2 + 3;
    return (
      <div
        key={i}
        className="sun-ray"
        style={{
          width: `${rayLength}px`,
          transform: `rotate(${angle}deg) translateX(${orbitRadius}px)`,
          '--lineLength': `${rayLength}px`,
          animation: `bounce ${randomDuration}s linear infinite`,
          animationDelay: `${randomDelay}s`
        }}
      />
    );
  });

  function toggleFullscreen() {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
    } else if (document.exitFullscreen) {
      document.exitFullscreen();
    }
  }

  return (
    <div id="homescreen">
      <div className="mountain-container">
        {mountains.map((m, i) => {
          const style = {
            position: "absolute",
            bottom: `-${m.height / 1.2}px`,
            left: m.left,
            width: `${m.width}px`,
            height: `${m.height}px`,
            background: `radial-gradient(circle at 50% 80%, #158b34 20%, #0f6f28 85%, #0b4d1e 100%)`,
            boxShadow: `0 20px 50px rgba(0, 0, 0, 0.4), inset 0 -20px 30px rgba(0, 0, 0, 0.5)`,
            borderRadius: "50% 50% 20% 20%",
            border: "8px solid rgba(0, 0, 0, 0.5)",
            zIndex: m.isFront ? 2 : 1
          };
          return <div key={i} style={style} />;
        })}
      </div>
      <div className="brick-wall"></div>
      <div className="sun">
        {rays}
      </div>
      <div className="fullscreen-icon">
        <div className="hitbox" onClick={toggleFullscreen}></div>
        <div className="corner top-left"></div>
        <div className="corner top-right"></div>
        <div className="corner bottom-right"></div>
        <div className="corner bottom-left"></div>
      </div>
      <div className="water">
        <svg viewBox="0 0 1200 200" preserveAspectRatio="none">
          <path
            d="M0 50 C300 150, 900 0, 1200 100 L1200 200 L0 200 Z"
            fill="#0af"
          >
            <animate
              attributeName="d"
              dur="10s"
              repeatCount="indefinite"
              values="M0 50 C300 150, 900 0, 1200 100 L1200 200 L0 200 Z; M0 55 C300 145, 900 5, 1200 95 L1200 200 L0 200 Z; M0 50 C300 150, 900 0, 1200 100 L1200 200 L0 200 Z"
            />
          </path>
          <g className="current-lines">
            {currents.map((current, i) => (
              <path
                key={i}
                d={current.d1}
                stroke="white"
                strokeWidth="0.8"
                fill="none"
              >
                <animateTransform
                  attributeName="transform"
                  type="translate"
                  from="-150 0"
                  to="1300 0"
                  dur={current.duration + "s"}
                  begin={current.delay + "s"}
                  fill="freeze"
                  restart="always"
                />
                <animate
                  attributeName="d"
                  values={`${current.d1};${current.d2};${current.d1}`}
                  dur="3s"
                  repeatCount="indefinite"
                />
                <animate
                  attributeName="opacity"
                  values="0;0.8;0"
                  keyTimes="0;0.5;1"
                  dur={current.duration + "s"}
                  begin={current.delay + "s"}
                  fill="freeze"
                />
              </path>
            ))}
          </g>
        </svg>
      </div>
    </div>
  );
}
