import React, { useState, useEffect } from 'react';
import "./HomeScreen.css";

export default function HomeScreen() {
  const [mountains, setMountains] = useState([]);

  useEffect(() => {
    let leftPos = 0;
    const arr = [];
    while (leftPos < 300) {
      const isFront = arr.length % 2 === 0;
      const h = isFront 
                ? Math.floor(Math.random() * 150) + 1400 
                : Math.floor(Math.random() * 150) + 2100;
      const w = Math.floor(Math.random() * 200) + 1450;
      const s = Math.random() * 2;
      const l = leftPos + s;
      arr.push({ height: h, width: w, left: `${l}%`, isFront });
      leftPos = l + 9.5;
    }
    setMountains(arr);
  }, []);

  function startGame() {
    console.log("test");
  }

  // Define ray parameters
  const rayCount = 12;
  const baseLength = 60; // base width for a ray
  const globalFactor = 0.5; // shorten all rays overall

  const rays = Array.from({ length: rayCount }, (_, i) => {
    const angle = i * (360 / rayCount);
    const rad = angle * (Math.PI / 180);
    // If cosine < 0, the ray is pointing left, so half the length; otherwise, double it.
    const sideFactor = Math.cos(rad) < 0 ? 0.5 : 2;
    const effectiveLength = baseLength * globalFactor * sideFactor;

    return (
      <div
        key={i}
        className="sun-ray"
        style={{
          width: `${effectiveLength}px`,
          transform: `rotate(${angle}deg) translateX(40px)` 
          // translateX(40px) positions the ray at the sun's edge (sun diameter is 80px)
        }}
      />
    );
  });

  return (
    <div id="homescreen">
      <h1>Game</h1>
      <button onClick={startGame}>Start</button>

      <div className="mountain-container">
        {mountains.map((m, i) => {
          const style = {
            position: "absolute",
            bottom: `-${m.height / 1.2}px`,
            left: m.left,
            width: `${m.width}px`,
            height: `${m.height}px`,
            background: `
              radial-gradient(
                circle at 50% 80%,
                #158b34 20%,
                #0f6f28 85%,
                #0b4d1e 100%
              )
            `,
            boxShadow: `
              0 20px 50px rgba(0, 0, 0, 0.4),
              inset 0 -20px 30px rgba(0, 0, 0, 0.5)
            `,
            borderRadius: "50% 50% 20% 20%",
            border: "8px solid rgba(0, 0, 0, 0.5)",
            zIndex: m.isFront ? 2 : -1,
          };
          return <div key={i} style={style} />;
        })}
      </div>

      <div className="sun">
        {rays}
      </div>
    </div>
  );
}
