import React, { useState, useEffect, useRef } from 'react';
import './HomeScreen.css';
import player1Img from './assets/player1.png';
import player2Img from './assets/player2.png';
import clouds from './assets/clouds.png';
import cans from './assets/cans.png';
import bottles from './assets/bottle.png';
import potion from './assets/potion.png';
import trash from './assets/trash.png';
import fish from './assets/fish.webp';
import heart from '../assets/heart.png';

export default function HomeScreen() {
  const [gameStarted, setGameStarted] = useState(false);
  const [score, setScore] = useState(0);
  const [health, setHealth] = useState(3);
  const [specialActive, setSpecialActive] = useState(false);
  const [gameObjects, setGameObjects] = useState([]);
  const [dynamicClouds, setDynamicClouds] = useState([]);
  const [sandHeight, setSandHeight] = useState(60);
  const [waterHovered, setWaterHovered] = useState(false);
  const player1Ref = useRef(null);
  const player2Ref = useRef(null);
  const keys = useRef({});
  const playerWidth = 62;
  const playerHeight = 80;
  const p1 = useRef({ pos: { x: window.innerWidth / 2 - playerWidth - 20, y: window.innerHeight / 2 - playerHeight / 2 }, vel: { x: 0, y: 0 }, grounded: false });
  const p2 = useRef({ pos: { x: window.innerWidth / 2 + 20, y: window.innerHeight / 2 - playerHeight / 2 }, vel: { x: 0, y: 0 }, grounded: false });
  const groundY = window.innerHeight - sandHeight;

  useEffect(() => {
    window.addEventListener('keydown', e => { keys.current[e.key] = true; });
    window.addEventListener('keyup', e => { keys.current[e.key] = false; });
    return () => {
      window.removeEventListener('keydown', e => { keys.current[e.key] = true; });
      window.removeEventListener('keyup', e => { keys.current[e.key] = false; });
    };
  }, []);

  useEffect(() => {
    if (!gameStarted) return;
    const loop = () => {
      if (keys.current['w'] || keys.current['W']) { if (p1.current.grounded) { p1.current.vel.y = -12; p1.current.grounded = false; } }
      if (keys.current['a'] || keys.current['A']) { p1.current.vel.x -= 0.4; }
      if (keys.current['d'] || keys.current['D']) { p1.current.vel.x += 0.4; }
      if (keys.current['ArrowUp']) { if (p2.current.grounded) { p2.current.vel.y = -12; p2.current.grounded = false; } }
      if (keys.current['ArrowLeft']) { p2.current.vel.x -= 0.4; }
      if (keys.current['ArrowRight']) { p2.current.vel.x += 0.4; }
      p1.current.vel.y += 0.15;
      p2.current.vel.y += 0.15;
      p1.current.vel.x *= 0.93;
      p1.current.vel.y *= 0.93;
      p2.current.vel.x *= 0.93;
      p2.current.vel.y *= 0.93;
      p1.current.pos.x += p1.current.vel.x;
      p1.current.pos.y += p1.current.vel.y;
      p2.current.pos.x += p2.current.vel.x;
      p2.current.pos.y += p2.current.vel.y;
      if (p1.current.pos.y + playerHeight > groundY) { p1.current.pos.y = groundY - playerHeight; p1.current.vel.y = 0; p1.current.grounded = true; }
      if (p2.current.pos.y + playerHeight > groundY) { p2.current.pos.y = groundY - playerHeight; p2.current.vel.y = 0; p2.current.grounded = true; }
      if (player1Ref.current) { player1Ref.current.style.left = `${p1.current.pos.x}px`; player1Ref.current.style.top = `${p1.current.pos.y}px`; }
      if (player2Ref.current) { player2Ref.current.style.left = `${p2.current.pos.x}px`; player2Ref.current.style.top = `${p2.current.pos.y}px`; }
      setGameObjects(prev => prev.filter(obj => obj.y < window.innerHeight + 50));
      prevObjectsCollision();
      requestAnimationFrame(loop);
    };
    const prevObjectsCollision = () => {
      setGameObjects(prev =>
        prev.filter(obj => {
          let collides = false;
          const checkCollision = (player) => {
            return !(player.pos.x + playerWidth < obj.x || player.pos.x > obj.x + obj.width || player.pos.y + playerHeight < obj.y || player.pos.y > obj.y + obj.height);
          };
          if (checkCollision(p1.current)) { handleCollision(obj, 1); collides = true; }
          if (checkCollision(p2.current)) { handleCollision(obj, 2); collides = true; }
          return !collides;
        })
      );
    };
    const handleCollision = (obj, playerNum) => {
      if (obj.type === 'can') { setScore(s => s + 10); }
      else if (obj.type === 'bottle') { setScore(s => s + 25); }
      else if (obj.type === 'trash') { setScore(s => s - 30); setHealth(h => h > 0 ? h - 1 : 0); triggerFlash(); }
      else if (obj.type === 'fish') { setScore(s => s - 5); setHealth(h => h > 0 ? h - 1 : 0); triggerFlash(); }
      else if (obj.type === 'potion') { setSpecialActive(true); shrinkPlayer(); }
    };
    const triggerFlash = () => {
      if (player1Ref.current) { player1Ref.current.classList.add('flash'); setTimeout(() => { player1Ref.current.classList.remove('flash'); }, 2000); }
      if (player2Ref.current) { player2Ref.current.classList.add('flash'); setTimeout(() => { player2Ref.current.classList.remove('flash'); }, 2000); }
    };
    const shrinkPlayer = () => {
      if (player1Ref.current) { player1Ref.current.classList.add('shrink'); }
      if (player2Ref.current) { player2Ref.current.classList.add('shrink'); }
      setTimeout(() => {
        if (player1Ref.current) { player1Ref.current.classList.remove('shrink'); }
        if (player2Ref.current) { player2Ref.current.classList.remove('shrink'); }
        setSpecialActive(false);
      }, 20000);
    };
    requestAnimationFrame(loop);
  }, [gameStarted, groundY]);

  useEffect(() => {
    if (!gameStarted) return;
    const spawnInterval = setInterval(() => {
      const typeRand = Math.random();
      let type = 'can';
      if (typeRand < 0.25) type = 'can';
      else if (typeRand < 0.5) type = 'bottle';
      else if (typeRand < 0.7) type = 'trash';
      else if (typeRand < 0.85) type = 'potion';
      else type = 'fish';
      let x, y, vx, vy, fromTop = true;
      if (type === 'fish') { fromTop = false; x = Math.random() < 0.5 ? -50 : window.innerWidth + 50; y = Math.random() * (window.innerHeight - 200); vx = x < 0 ? Math.random() * 2 + 1 : -(Math.random() * 2 + 1); vy = 0; }
      else { x = Math.random() * (window.innerWidth - 50); y = -50; vx = (Math.random()-0.5)*2; vy = Math.random()*2+1; }
      let size = 40;
      if (type === 'trash' || type === 'bottle') size = 50;
      if (type === 'fish') size = 60;
      setGameObjects(prev => [...prev, { id: Date.now() + Math.random(), type, x, y, vx, vy, width: size, height: size, fromTop }]);
    }, 2000);
    return () => clearInterval(spawnInterval);
  }, [gameStarted]);

  useEffect(() => {
    if (!gameStarted) return;
    const cloudInterval = setInterval(() => {
      const id = Date.now() + Math.random();
      const newCloud = { id, top: Math.random()*30+5, width: 100+Math.random()*50, duration: 40+Math.random()*20 };
      setDynamicClouds(prev => [...prev, newCloud]);
      setTimeout(() => { setDynamicClouds(prev => prev.filter(c => c.id !== id)); }, newCloud.duration*1000);
    }, 500);
    return () => clearInterval(cloudInterval);
  }, [gameStarted]);

  return gameStarted ? (
    <div id="game" style={{position:'relative',width:'100vw',height:'100vh',overflow:'hidden',background:'#66ccff'}}>
      <div className="score-counter">{score}</div>
      <div className="hearts">
        {Array.from({length:3}).map((_,i)=><img key={i} src={heart} alt="heart" className={i<health ? "heart" : "heart faded"} />)}
      </div>
      <div className="bubbles">
        {Array.from({length:15}).map((_,i)=>
          <div key={i} className="bubble" style={{left:`${Math.random()*100}%`,top:`${Math.random()*100}%`,width:`${10+Math.random()*20}px`,height:`${10+Math.random()*20}px`}}></div>
        )}
      </div>
      {gameObjects.map(obj => {
        let src = cans;
        if(obj.type==='bottle') src = bottles;
        else if(obj.type==='potion') src = potion;
        else if(obj.type==='trash') src = trash;
        else if(obj.type==='fish') src = fish;
        return <img key={obj.id} src={src} alt={obj.type} style={{position:'absolute',left:obj.x,top:obj.y,width:obj.width,height:obj.height}} />
      })}
      <img ref={player1Ref} src={player1Img} alt="Player1" className="player" style={{width:playerWidth, height:playerHeight}} />
      <img ref={player2Ref} src={player2Img} alt="Player2" className="player" style={{width:playerWidth, height:playerHeight}} />
      <div className="sand" style={{position:'absolute',bottom:0,left:0,width:'100%',height:sandHeight,background:'#f4a460'}}></div>
    </div>
  ) : (
    <div id="homescreen">
      <div className="clouds-container">
        {dynamicClouds.map(cloud => (
          <img key={cloud.id} src={clouds} alt="cloud" className="cloud" style={{top:`${cloud.top}%`, left:`-${cloud.width}px`, width:cloud.width, animationDuration:`${cloud.duration}s`}} />
        ))}
      </div>
      <div className="mountain-container">
        {Array.from({length:5}).map((_,i)=>
          <div key={i} style={{position:'absolute',bottom:'-200px',left:`${i*20}%`,width:'300px',height:'800px',background:'gray'}}></div>
        )}
      </div>
      <div className="brick-wall"></div>
      <div className="overlay">
        <div className="text-container">
          <div className="top-text">ECO</div>
          <div className="bottom-text">RESCUE</div>
        </div>
        <div className="pyramid-buttons">
          <button className="menu-button" onClick={()=>setGameStarted(true)}>Start</button>
          <div className="lower-buttons">
            <button className="menu-button">Settings</button>
            <button className="menu-button">Keybinds</button>
          </div>
        </div>
      </div>
      <div className="water">
        <svg viewBox="0 0 1200 800" preserveAspectRatio="none">
          <path d="M0 300 C 200 350, 400 250, 600 300 C 800 350, 1000 250, 1200 300 L1200 800 L0 800 Z" fill="#0af" fillOpacity="0.6" />
        </svg>
      </div>
    </div>
  );
}
