import React, { useState, useEffect, useRef, useCallback } from 'react';
import "./HomeScreen.css";

export default function HomeScreen() {
  const [mountains, setMountains] = useState([]);
  const [whiteCurrents, setWhiteCurrents] = useState([]);
  const whiteCurrentId = useRef(0);

  const generateMountains = () => {
    let leftPos = 0;
    const arr = [];
    while (leftPos < 300) {
      const isFront = arr.length % 2 === 0;
      const h = isFront 
        ? Math.floor(Math.random() * 150) + 1750 
        : Math.floor(Math.random() * 150) + 2650;
      const w = Math.floor(Math.random() * 200) + 1900;
      const s = Math.random() * 3;
      const l = leftPos + s;
      arr.push({ height: h, width: w, left: `${l}%`, isFront });
      leftPos = l + 9.5;
    }
    const mountainBefore = {
      height: Math.floor(Math.random() * 150) + 1750,
      width: Math.floor(Math.random() * 200) + 1900,
      left: "-5%",
      isFront: true
    };
    const mountainAfter = {
      height: Math.floor(Math.random() * 150) + 1750,
      width: Math.floor(Math.random() * 200) + 1900,
      left: "305%",
      isFront: false
    };
    arr.unshift(mountainBefore);
    arr.push(mountainAfter);
    return arr;
  };

  useEffect(() => {
    setMountains(generateMountains());
  }, []);

  // Sun rays (unchanged)
  const rayCount = 18;
  const orbitRadius = 46;
  const minRayLength = 20;
  const maxRayLength = 25;
  const rays = Array.from({ length: rayCount }, (_, i) => {
    const angle = i * (360 / rayCount);
    const rayLength = Math.random() * (maxRayLength - minRayLength) + minRayLength;
    const randomDelay = Math.random() * 8;
    const randomDuration = Math.random() * 2 + 3;
    return (
      <div
        key={i}
        className="sun-ray"
        style={{
          width: `${rayLength}px`,
          transform: `rotate(${angle}deg) translateX(${orbitRadius}px)`,
          '--lineLength': `${rayLength}px`,
          animation: `bounce ${randomDuration}s linear infinite`,
          animationDelay: `${randomDelay}s`
        }}
      />
    );
  });

  function toggleFullscreen() {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
    } else if (document.exitFullscreen) {
      document.exitFullscreen();
    }
  }

  // Generates a smooth, realistic static wave path for the water's top curve.
  // Baseline is set at y = 300 using 6 segments with cubic Bezier curves.
  function generateWavePath(shift) {
    const baseline = 300;
    let d = `M0 ${baseline}`;
    const segments = 6;
    const segmentWidth = 1200 / segments;
    for (let i = 0; i < segments; i++) {
      const x0 = i * segmentWidth;
      const x1 = x0 + segmentWidth / 3;
      const x2 = x0 + (2 * segmentWidth) / 3;
      const x3 = (i + 1) * segmentWidth;
      const amplitude = 20 * (i % 2 === 0 ? 1 : -1);
      const cp1X = x1 + shift;
      const cp2X = x2 + shift;
      const cp1Y = baseline + amplitude;
      const cp2Y = baseline + amplitude;
      d += ` C ${cp1X.toFixed(1)} ${cp1Y.toFixed(1)}, ${cp2X.toFixed(1)} ${cp2Y.toFixed(1)}, ${x3} ${baseline}`;
    }
    d += ` L1200 800 L0 800 Z`;
    return d;
  }

  // Static water wave (no animation on water)
  const d1 = generateWavePath(0);

  // Generates a short (~200px), curvy white current line.
  // The baseline is provided.
  function generateWhiteLinePath(baseline) {
    const segmentWidth = 200 / 2; // 2 segments, ~100px each
    const cp1X = Math.random() * segmentWidth;
    const cp1Y = baseline + (Math.random() * 20 - 10);
    const cp2X = segmentWidth + Math.random() * segmentWidth;
    const cp2Y = baseline + (Math.random() * 20 - 10);
    return `M0 ${baseline} Q ${cp1X.toFixed(1)} ${cp1Y.toFixed(1)} ${segmentWidth} ${baseline} Q ${cp2X.toFixed(1)} ${cp2Y.toFixed(1)} 200 ${baseline}`;
  }

  // Wrap spawnWhiteCurrent in useCallback to keep it stable for useEffect
  const spawnWhiteCurrent = useCallback(() => {
    const id = whiteCurrentId.current++;
    // Helper: check if a candidate baseline is too close (50px apart now)
    const isCandidateTooClose = (candidate) =>
      whiteCurrents.some((current) => Math.abs(current.baseline - candidate) < 50);

    let candidate = Math.random() * 100 + 600; // candidate between 600 and 700
    let tries = 0;
    while (isCandidateTooClose(candidate) && tries < 10) {
      candidate = Math.random() * 100 + 600;
      tries++;
    }
    const d = generateWhiteLinePath(candidate);
    // Random duration between 4 and 10 seconds.
    const duration = Math.random() * 6 + 4;
    const lifetime = duration >= 10 ? 10000 : duration * 1000;
    const newCurrent = { id, d, duration, baseline: candidate };
    setWhiteCurrents((prev) => [...prev, newCurrent]);
    setTimeout(() => {
      setWhiteCurrents((prev) => prev.filter((current) => current.id !== id));
    }, lifetime);
  }, [whiteCurrents]);

  useEffect(() => {
    // Schedule white current spawns at random intervals between 0.5 and 1.5 seconds.
    function scheduleNextWhiteCurrent() {
      const delay = Math.random() * 1000 + 500; // 500 to 1500 ms
      const timer = setTimeout(() => {
        spawnWhiteCurrent();
        scheduleNextWhiteCurrent();
      }, delay);
      return timer;
    }
    const timer = scheduleNextWhiteCurrent();
    return () => clearTimeout(timer);
  }, [spawnWhiteCurrent]);

  return (
    <div id="homescreen">
      <div className="mountain-container">
        {mountains.map((m, i) => {
          const style = {
            position: "absolute",
            bottom: `-${m.height / 1.2}px`,
            left: m.left,
            width: `${m.width}px`,
            height: `${m.height}px`,
            background: `radial-gradient(circle at 50% 80%, #158b34 20%, #0f6f28 85%, #0b4d1e 100%)`,
            boxShadow: `0 20px 50px rgba(0, 0, 0, 0.4), inset 0 -20px 30px rgba(0, 0, 0, 0.5)`,
            borderRadius: "50% 50% 20% 20%",
            border: "8px solid rgba(0, 0, 0, 0.5)",
            zIndex: m.isFront ? 2 : 1
          };
          return <div key={i} style={style} />;
        })}
      </div>
      <div className="brick-wall"></div>
      <div className="sun">
        {rays}
      </div>
      <div className="fullscreen-icon">
        <div className="hitbox" onClick={toggleFullscreen}></div>
        <div className="corner top-left"></div>
        <div className="corner top-right"></div>
        <div className="corner bottom-right"></div>
        <div className="corner bottom-left"></div>
      </div>
      {/* Fixed water container at the bottom */}
      <div className="water" style={{ position: "absolute", bottom: 0, left: 0, width: "100%" }}>
        <svg viewBox="0 0 1200 800" preserveAspectRatio="none">
          <path d={d1} fill="#0af" fillOpacity="0.6" />
          {/* Render white current lines */}
          {whiteCurrents.map((current) => (
            <path key={current.id} d={current.d} stroke="white" strokeWidth="1" fill="none">
              <animateTransform 
                attributeName="transform" 
                type="translate" 
                from="-200 0" 
                to="1400 0" 
                dur={`${current.duration}s`} 
                fill="freeze" 
              />
            </path>
          ))}
        </svg>
      </div>
    </div>
  );
}
