import React, { useState, useEffect, useRef } from 'react';
import "./HomeScreen.css";

export default function HomeScreen() {
  const [mountains, setMountains] = useState([]);
  const [currents, setCurrents] = useState([]);
  const currentIdRef = useRef(0);
  const currentIntervalId = useRef(null);

  const generateMountains = () => {
    let leftPos = 0;
    const arr = [];
    while (leftPos < 300) {
      const isFront = arr.length % 2 === 0;
      const h = isFront ? Math.floor(Math.random() * 150) + 1750 : Math.floor(Math.random() * 150) + 2650;
      const w = Math.floor(Math.random() * 200) + 1900;
      const s = Math.random() * 3;
      const l = leftPos + s;
      arr.push({ height: h, width: w, left: `${l}%`, isFront });
      leftPos = l + 9.5;
    }
    const mountainBefore = {
      height: Math.floor(Math.random() * 150) + 1750,
      width: Math.floor(Math.random() * 200) + 1900,
      left: "-5%",
      isFront: true
    };
    const mountainAfter = {
      height: Math.floor(Math.random() * 150) + 1750,
      width: Math.floor(Math.random() * 200) + 1900,
      left: "305%",
      isFront: false
    };
    arr.unshift(mountainBefore);
    arr.push(mountainAfter);
    return arr;
  };

  // Function to spawn a current line that animates from left to right and is then removed.
  const spawnCurrentLine = () => {
    const id = currentIdRef.current++;
    const startX = 0; // starting coordinate (animation will translate from left to right)
    const deltaX = Math.random() * 100 + 200; // width between 200 and 300
    const endX = startX + deltaX;
    // Keep Y near the water level (around 50)
    const startY = 50 + (Math.random() * 10 - 5);
    const endY = startY + (Math.random() * 10 - 5);
    const cp1X = startX + deltaX / 3 + (Math.random() * 20 - 10);
    const cp1Y = startY + (Math.random() * 10 - 5);
    const cp2X = startX + (2 * deltaX) / 3 + (Math.random() * 20 - 10);
    const cp2Y = endY + (Math.random() * 10 - 5);
    const d1 = `M${startX.toFixed(1)},${startY.toFixed(1)} C${cp1X.toFixed(1)},${cp1Y.toFixed(1)} ${cp2X.toFixed(1)},${cp2Y.toFixed(1)} ${endX.toFixed(1)},${endY.toFixed(1)}`;

    // Alternate curve for animation effect.
    const cp1X2 = startX + deltaX / 3 + (Math.random() * 20 - 10);
    const cp1Y2 = startY + (Math.random() * 10 - 5);
    const cp2X2 = startX + (2 * deltaX) / 3 + (Math.random() * 20 - 10);
    const cp2Y2 = endY + (Math.random() * 10 - 5);
    const d2 = `M${startX.toFixed(1)},${startY.toFixed(1)} C${cp1X2.toFixed(1)},${cp1Y2.toFixed(1)} ${cp2X2.toFixed(1)},${cp2Y2.toFixed(1)} ${endX.toFixed(1)},${endY.toFixed(1)}`;

    const duration = (Math.random() * 4 + 4).toFixed(1); // 4 to 8 seconds
    const delay = 0; // spawn immediately

    const newCurrent = { id, d1, d2, duration, delay };
    setCurrents(prev => [...prev, newCurrent]);

    // Remove this current after its animation completes.
    const totalTime = (parseFloat(duration) + parseFloat(delay)) * 1000 + 100; // slight buffer
    setTimeout(() => {
      setCurrents(prev => prev.filter(line => line.id !== id));
    }, totalTime);
  };

  useEffect(() => {
    setMountains(generateMountains());

    // Spawn a new current line every 2 seconds.
    currentIntervalId.current = setInterval(() => {
      spawnCurrentLine();
    }, 2000);

    return () => {
      clearInterval(currentIntervalId.current);
    };
  }, []);

  const rayCount = 18;
  const orbitRadius = 46;
  const minRayLength = 20;
  const maxRayLength = 25;
  const rays = Array.from({ length: rayCount }, (_, i) => {
    const angle = i * (360 / rayCount);
    const rayLength = Math.random() * (maxRayLength - minRayLength) + minRayLength;
    const randomDelay = Math.random() * 8;
    const randomDuration = Math.random() * 2 + 3;
    return (
      <div
        key={i}
        className="sun-ray"
        style={{
          width: `${rayLength}px`,
          transform: `rotate(${angle}deg) translateX(${orbitRadius}px)`,
          '--lineLength': `${rayLength}px`,
          animation: `bounce ${randomDuration}s linear infinite`,
          animationDelay: `${randomDelay}s`
        }}
      />
    );
  });

  function toggleFullscreen() {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
    } else if (document.exitFullscreen) {
      document.exitFullscreen();
    }
  }

  return (
    <div id="homescreen">
      <div className="mountain-container">
        {mountains.map((m, i) => {
          const style = {
            position: "absolute",
            bottom: `-${m.height / 1.2}px`,
            left: m.left,
            width: `${m.width}px`,
            height: `${m.height}px`,
            background: `radial-gradient(circle at 50% 80%, #158b34 20%, #0f6f28 85%, #0b4d1e 100%)`,
            boxShadow: `0 20px 50px rgba(0, 0, 0, 0.4), inset 0 -20px 30px rgba(0, 0, 0, 0.5)`,
            borderRadius: "50% 50% 20% 20%",
            border: "8px solid rgba(0, 0, 0, 0.5)",
            zIndex: m.isFront ? 2 : 1
          };
          return <div key={i} style={style} />;
        })}
      </div>
      <div className="brick-wall"></div>
      <div className="sun">
        {rays}
      </div>
      <div className="fullscreen-icon">
        <div className="hitbox" onClick={toggleFullscreen}></div>
        <div className="corner top-left"></div>
        <div className="corner top-right"></div>
        <div className="corner bottom-right"></div>
        <div className="corner bottom-left"></div>
      </div>
      <div className="water">
        <svg viewBox="0 0 1200 200" preserveAspectRatio="none">
          <path
            d="M0 50 C300 50, 900 50, 1200 50 L1200 200 L0 200 Z"
            fill="#0af"
          >
            {/* Water now animates with dynamic curves moving up and down */}
            <animate
              attributeName="d"
              dur="10s"
              repeatCount="indefinite"
              values="
                M0 50 C300 40, 900 60, 1200 50 L1200 200 L0 200 Z;
                M0 50 C300 60, 900 40, 1200 50 L1200 200 L0 200 Z;
                M0 50 C300 45, 900 55, 1200 50 L1200 200 L0 200 Z;
                M0 50 C300 40, 900 60, 1200 50 L1200 200 L0 200 Z
              "
            />
          </path>
          <g className="current-lines">
            {currents.map((current) => (
              <path
                key={current.id}
                d={current.d1}
                stroke="white"
                strokeWidth="0.8"
                fill="none"
              >
                {/* Animate current lines from left to right */}
                <animateTransform
                  attributeName="transform"
                  type="translate"
                  from="-150 0"
                  to="1300 0"
                  dur={current.duration + "s"}
                  begin={current.delay + "s"}
                  fill="freeze"
                />
                <animate
                  attributeName="d"
                  values={`${current.d1};${current.d2};${current.d1}`}
                  dur={current.duration + "s"}
                  fill="freeze"
                />
                <animate
                  attributeName="opacity"
                  values="0;0.8;0"
                  keyTimes="0;0.5;1"
                  dur={current.duration + "s"}
                  begin={current.delay + "s"}
                  fill="freeze"
                />
              </path>
            ))}
          </g>
        </svg>
      </div>
    </div>
  );
}
